import React, { useState } from 'react';
import { Form, Input, Button, Select, Upload, message, Card } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import axios from 'axios';

const { Option } = Select;

const Training: React.FC = () => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);

  const onFinish = async (values: any) => {
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:8000/train', {
        tag: values.tag,
        type: values.type,
        input_data: values.input_data,
        metadata: {
          description: values.description
        }
      });
      message.success('训练成功！');
      console.log('训练结果:', response.data);
    } catch (error) {
      message.error('训练失败：' + error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card title="参数训练">
      <Form
        form={form}
        layout="vertical"
        onFinish={onFinish}
      >
        <Form.Item
          name="tag"
          label="算法标识"
          rules={[{ required: true, message: '请输入算法标识' }]}
        >
          <Input placeholder="请输入算法标识" />
        </Form.Item>

        <Form.Item
          name="type"
          label="参数类型"
          rules={[{ required: true, message: '请选择参数类型' }]}
        >
          <Select placeholder="请选择参数类型">
            <Option value="normal">普通参数</Option>
            <Option value="top">Top参数</Option>
          </Select>
        </Form.Item>

        <Form.Item
          name="description"
          label="描述"
        >
          <Input.TextArea placeholder="请输入算法描述" />
        </Form.Item>

        <Form.Item
          name="input_data"
          label="训练数据"
          rules={[{ required: true, message: '请上传训练数据' }]}
        >
          <Upload>
            <Button icon={<UploadOutlined />}>上传训练数据</Button>
          </Upload>
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading}>
            开始训练
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );
};

export default Training;
