import React, { useState } from 'react';
import { Form, Button, message, Card, Upload } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import type { UploadFile, UploadProps } from 'antd/es/upload/interface'; // Import Upload related types
import axios from 'axios';

const Training: React.FC = () => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [trainingResult, setTrainingResult] = useState<any>(null);
  const [fileList, setFileList] = useState<UploadFile[]>([]); // State for uploaded file list
  const [jsonParsedData, setJsonParsedData] = useState<any>(null); // State for parsed JSON data

  const onFinish = async (values: any) => {
    if (!jsonParsedData) {
      message.error('请先上传并验证训练数据文件。');
      return;
    }

    try {
      setLoading(true);
      
      // jsonParsedData 已经包含了 Name 和 Input_x
      const payload = jsonParsedData;

      const response = await axios.post('/api/train', payload);
      message.success('训练成功！');
      setTrainingResult(response.data.parameters);
      console.log('训练结果:', response.data);
    } catch (error: any) {
      console.error('训练失败:', error);
      message.error('训练失败：' + (error.response?.data?.detail || error.message || String(error)));
      setTrainingResult(null);
    } finally {
      setLoading(false);
    }
  };

  const uploadProps: UploadProps = {
    name: 'file',
    accept: '.json',
    maxCount: 1,
    fileList: fileList,
    onRemove: (file) => {
      const index = fileList.indexOf(file);
      const newFileList = fileList.slice();
      newFileList.splice(index, 1);
      setFileList(newFileList);
      setJsonParsedData(null); // Clear parsed data
      message.info(`已移除文件: ${file.name}`);
      return true;
    },
    beforeUpload: (file) => {
      const isJson = file.type === 'application/json' || file.name.endsWith('.json');
      if (!isJson) {
        message.error('只能上传 JSON 文件!');
        return Upload.LIST_IGNORE; // Prevent upload and show error
      }

      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = (e) => {
          try {
            let content = e.target?.result as string;
            if (content.charCodeAt(0) === 0xFEFF) {
              content = content.substring(1);
            }
            content = content.replace(/[\uFEFF\u00A0\u200B]/g, '').trim();

            const parsed = JSON.parse(content);
            if (!parsed.Name || !parsed.Input_x || !Array.isArray(parsed.Input_x)) {
              const errorMessage = 'JSON 格式不符合预期：需要包含 &quot;Name&quot; 和 &quot;Input_x&quot; 数组。';
              message.error(errorMessage);
              setJsonParsedData(null);
              console.error('Validation failed:', errorMessage);
              return reject(new Error(errorMessage)); // Pass Error object
            }
            for (const item of parsed.Input_x) {
              if (!item.hasOwnProperty('x') || !Array.isArray(item.x) || !item.hasOwnProperty('y') || typeof item.y !== 'number') {
                  const errorMessage = 'Input_x 数组中的每个项目必须包含 &quot;x&quot; (数组) 和 &quot;y&quot; (数字)。';
                  message.error(errorMessage);
                  setJsonParsedData(null);
                  console.error('Validation failed:', errorMessage, item);
                  return reject(new Error(errorMessage)); // Pass Error object
              }
            }

            setJsonParsedData(parsed);
            setFileList([file]);
            message.success(`${file.name} 文件解析成功，可以开始训练。`);
            resolve(false); // Resolve with false to prevent default upload, as we handle it manually
          } catch (error) {
            const errorMessage = `文件解析失败: ${error instanceof Error ? error.message : String(error)}`;
            message.error(errorMessage);
            setJsonParsedData(null);
            console.error('Parsing failed:', error);
            reject(new Error(errorMessage)); // Pass Error object
          }
        };
        reader.onerror = (error) => {
          const errorMessage = `文件读取失败: ${error}`;
          message.error(errorMessage);
          setJsonParsedData(null);
          console.error('Read failed:', error);
          reject(new Error(errorMessage)); // Pass Error object
        };

        reader.readAsText(file, 'utf-8');
      });
    },
  };

  return (
    <Card title="参数训练">
      <Form
        form={form}
        layout="vertical"
        onFinish={onFinish}
      >
        <Form.Item
          label="训练数据集 (JSON 文件)"
          rules={[{ required: true, message: '请上传训练数据文件' }]} // Validation for file upload
        >
          <Upload {...uploadProps}>
            <Button icon={<UploadOutlined />} disabled={fileList.length > 0}>上传训练数据集 (仅限 JSON)</Button>
          </Upload>
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading} disabled={!jsonParsedData}>
            开始训练
          </Button>
        </Form.Item>
      </Form>

      {trainingResult && (
        <Card title="训练结果" style={{ marginTop: '20px' }}>
          <p><strong>训练Tag:</strong> {trainingResult.tag || trainingResult.name}</p>
          <p><strong>系数 (w):</strong> {JSON.stringify(trainingResult.w)}</p>
          <p><strong>截距 (b):</strong> {trainingResult.b}</p>
        </Card>
      )}
    </Card>
  );
};

export default Training;
