import React from 'react';
import { Form, Input, Button, Card, Switch } from 'antd';

const Settings: React.FC = () => {
  const [form] = Form.useForm();

  const onFinish = (values: any) => {
    console.log('设置已保存:', values);
  };

  return (
    <Card title="系统设置">
      <Form
        form={form}
        layout="vertical"
        onFinish={onFinish}
        initialValues={{
          apiUrl: 'http://localhost:8000',
          enableAutoSave: true,
          enableLogging: true
        }}
      >
        <Form.Item
          name="apiUrl"
          label="API地址"
          rules={[{ required: true, message: '请输入API地址' }]}
        >
          <Input placeholder="请输入API地址" />
        </Form.Item>

        <Form.Item
          name="enableAutoSave"
          label="自动保存"
          valuePropName="checked"
        >
          <Switch />
        </Form.Item>

        <Form.Item
          name="enableLogging"
          label="启用日志"
          valuePropName="checked"
        >
          <Switch />
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit">
            保存设置
          </Button>
        </Form.Item>
      </Form>
    </Card>
  );
};

export default Settings;
