import React, { useState } from 'react';
import { Form, Button, message, Card, Upload } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import type { UploadFile, UploadProps } from 'antd/es/upload/interface';
import axios from 'axios';

const Training: React.FC = () => {
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);
  const [trainingResult, setTrainingResult] = useState<any>(null);
  const [fileList, setFileList] = useState<UploadFile[]>([]); // State for uploaded file list

  const onFinish = async () => {
    if (fileList.length === 0) {
      message.error('请先上传训练数据文件。');
      return;
    }

    try {
      setLoading(true);
      
      const formData = new FormData();
      formData.append('file', fileList[0].originFileObj as Blob); // Append the original file object

      // Send as multipart/form-data
      const response = await axios.post('/api/train', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      message.success('训练成功！');
      setTrainingResult(response.data.parameters);
      console.log('训练结果:', response.data);
    } catch (error: any) {
      console.error('训练失败:', error);
      message.error('训练失败：' + (error.response?.data?.detail || error.message || String(error)));
      setTrainingResult(null);
    } finally {
      setLoading(false);
    }
  };

  const uploadProps: UploadProps = {
    name: 'file',
    accept: '.json',
    maxCount: 1,
    fileList: fileList,
    onRemove: (file) => {
      const newFileList = fileList.filter((item) => item.uid !== file.uid);
      setFileList(newFileList);
      message.info(`已移除文件: ${file.name}`);
      return true;
    },
    beforeUpload: (file) => {
      const isJson = file.type === 'application/json' || file.name.endsWith('.json');
      if (!isJson) {
        message.error('只能上传 JSON 文件!');
        return Upload.LIST_IGNORE; // Prevent upload and show error
      }

      // No FileReader or JSON parsing here, just set the file for onFinish
      setFileList([file]);
      message.success(`${file.name} 文件已选择，点击 &quot;开始训练&quot;。`);
      return false; // Prevent Ant Design Upload from sending the file directly
    },
  };

  return (
    <Card title="参数训练">
      <Form
        form={form}
        layout="vertical"
        onFinish={onFinish}
      >
        <Form.Item
          label="训练数据集 (JSON 文件)"
          rules={[{ required: true, message: '请上传训练数据文件' }]}
        >
          <Upload {...uploadProps}>
            <Button icon={<UploadOutlined />} disabled={fileList.length > 0}>上传训练数据集 (仅限 JSON)</Button>
          </Upload>
        </Form.Item>

        <Form.Item>
          <Button type="primary" htmlType="submit" loading={loading} disabled={fileList.length === 0}>
            开始训练
          </Button>
        </Form.Item>
      </Form>

      {trainingResult && (
        <Card title="训练结果" style={{ marginTop: '20px' }}>
          <p><strong>训练Tag:</strong> {trainingResult.tag || trainingResult.name}</p>
          <p><strong>系数 (w):</strong> {JSON.stringify(trainingResult.w)}</p>
          <p><strong>截距 (b):</strong> {trainingResult.b}</p>
        </Card>
      )}
    </Card>
  );
};

export default Training;
