import React, { useState } from 'react';
import { Card, Row, Col, Statistic, Button, message } from 'antd';
import { ExperimentOutlined, FileTextOutlined, SettingOutlined, ApiOutlined } from '@ant-design/icons';

const Home: React.FC = () => {
  const [backendMessage, setBackendMessage] = useState<string>('未连接');

  const testBackendConnection = async () => {
    try {
      const response = await fetch('/api/test'); // 假设后端运行在相同域名和端口
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setBackendMessage(data.message);
      message.success('后端连接成功！');
    } catch (error) {
      console.error('Error testing backend connection:', error);
      setBackendMessage('连接失败');
      message.error('后端连接失败！请检查后端服务是否已启动。');
    }
  };

  return (
    <div>
      <h1>参数拟合工具</h1>
      <Row gutter={16}>
        <Col span={8}>
          <Card>
            <Statistic
              title="已训练参数"
              value={0}
              prefix={<ExperimentOutlined />}
            />
          </Card>
        </Col>
        <Col span={8}>
          <Card>
            <Statistic
              title="数据集数量"
              value={0}
              prefix={<FileTextOutlined />}
            />
          </Card>
        </Col>
        <Col span={8}>
          <Card>
            <Statistic
              title="配置项"
              value={0}
              prefix={<SettingOutlined />}
            />
          </Card>
        </Col>
      </Row>
      <div style={{ marginTop: '20px', textAlign: 'center' }}>
        <Button
          type="primary"
          icon={<ApiOutlined />}
          onClick={testBackendConnection}
        >
          测试后端连接
        </Button>
        <p style={{ marginTop: '10px' }}>后端状态: {backendMessage}</p>
      </div>
    </div>
  );
};

export default Home;
