/* ==================== Top_Training.tsx ==================== */
import React, { useState, useRef } from 'react';
import { Card, Upload, Button, message, Space } from 'antd';
import { UploadOutlined, DownloadOutlined } from '@ant-design/icons';
import type { UploadProps, UploadFile } from 'antd';
import type { RcFile } from 'antd/es/upload';
import axios from 'axios';

/* -------------------- 组件主体 -------------------- */
const Top_Training: React.FC = () => {
  /* Tag.json 状态 ------------------------------------------------ */
  const [tagFileName, setTagFileName] = useState<string | null>(null);
  const [tagPath, setTagPath]         = useState<string | null>(null);
  const [uploadingTag, setUploadingTag] = useState(false);
  const tagFileRef = useRef<RcFile | null>(null);              // ⭐ 保存 File 对象

  /* Top 训练数据集状态 ------------------------------------------- */
  const trainFileRef = useRef<RcFile | null>(null);
  const [trainName, setTrainName]     = useState<string | null>(null);

  /* 训练过程 ---------------------------------------------------- */
  const [loading, setLoading]         = useState(false);
  const [topResult, setTopResult]     = useState<any>(null);

  /* ---------- 上传 / 切换 Tag.json ---------- */
  const uploadTag: UploadProps['customRequest'] = async (opt) => {
    /* 把 file 先断言成 RcFile ↓↓↓ */
    const rcFile = opt.file as RcFile;
    const { onSuccess, onError } = opt;
  
    setUploadingTag(true);
    try {
      const fd = new FormData();
      fd.append('file', rcFile, rcFile.name);
  
      const { data } = await axios.post('/api/upload-tag-json', fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
  
      tagFileRef.current = rcFile;
      setTagFileName(rcFile.name);
      setTagPath(data.active_file || data.active);
  
      /* 这里用 rcFile.name 就不会报错 */
      message.success(`${rcFile.name} 已设为当前 Tag`);
  
      onSuccess && onSuccess({}, {});
    } catch (err: any) {
      message.error(`Tag 上传失败：${err?.response?.data?.detail || err.message}`);
      onError && onError(err);
    } finally {
      setUploadingTag(false);
    }
  };

  /* ---------- 本地文件下载助手 ---------- */
  const downloadLocal = (file: RcFile | null) => {
    if (!file) return;
    const url = URL.createObjectURL(file);
    const a = document.createElement('a');
    a.href = url;
    a.download = file.name;
    a.style.display = 'none';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  /* ---------- 点击开始训练 ---------- */
  const startTrain = async () => {
    if (!tagPath) { message.error('请先上传 Tag 文件'); return; }
    if (!trainFileRef.current) { message.error('请上传训练数据集'); return; }

    setLoading(true);
    try {
      /* 自动识别编码读取训练数据 */
      const buf = await trainFileRef.current.arrayBuffer();
      let txt: string;
      try { txt = new TextDecoder('utf-8').decode(buf); JSON.parse(txt); }
      catch { txt = new TextDecoder('utf-16le').decode(buf); }
      const jsonData = JSON.parse(txt);                        // {name, Top_Tags, input_x}

      const fd = new FormData();
      fd.append('data_json', JSON.stringify(jsonData));
      fd.append('tag_path',  tagPath);

      const { data } = await axios.post('/api/train_top', fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      message.success(data.message || '训练成功！');
      setTopResult(data);                                      // {name, Top_Tags, Top_Params}
    } catch (err: any) {
      message.error(`训练失败：${err?.response?.data?.detail || err.message}`);
      setTopResult(null);
    } finally {
      setLoading(false);
    }
  };

  /* ---------- Upload 配置 ---------- */
  const tagUploadProps: UploadProps = {
    accept: '.json',
    maxCount: 1,
    customRequest: uploadTag,
    onRemove: () => { setTagFileName(null); setTagPath(null); tagFileRef.current = null; return true; },
    fileList: tagFileName ? [{
      uid: 'tag', name: tagFileName, status: 'done' as UploadFile['status'],
    }] : [],
  };

  const dataUploadProps: UploadProps = {
    accept: '.json',
    maxCount: 1,
    customRequest: (opt: any) => {
      const { file, onSuccess } = opt;
      trainFileRef.current = file as RcFile;
      setTrainName((file as RcFile).name);
      message.success(`${file.name} 已就绪，点击“开始训练”`);
      onSuccess && onSuccess({}, {} as any);
    },
    onRemove: () => { trainFileRef.current = null; setTrainName(null); return true; },
    fileList: trainName ? [{
      uid: 'data', name: trainName, status: 'done' as UploadFile['status'],
    }] : [],
  };

  /* ---------- JSX ---------- */
  return (
    <Card title="Top 参数训练">
      {/* Tag 上传区 */}
      <Upload {...tagUploadProps}>
        <Button icon={<UploadOutlined />} loading={uploadingTag}
                disabled={uploadingTag || !!tagFileName}>
          上传 / 切换 Tag 文件
        </Button>
      </Upload>

      {/* Tag 文件信息 + 下载按钮 */}
      {tagFileName && (
        <Space style={{ marginTop: 8 }}>
          <span>当前 Tag: {tagFileName}</span>
          <Button size="small" icon={<DownloadOutlined />}
                  onClick={() => downloadLocal(tagFileRef.current)}>
            下载
          </Button>
        </Space>
      )}

      {/* 训练数据集上传区 */}
      <Upload {...dataUploadProps} style={{ marginTop: 16 }}>
        <Button icon={<UploadOutlined />} disabled={!!trainName}>
          上传 Top 训练数据集
        </Button>
      </Upload>

      {/* 数据集信息 + 下载按钮 */}
      {trainName && (
        <Space style={{ marginTop: 8 }}>
          <span>已选择数据集: {trainName}</span>
          <Button size="small" icon={<DownloadOutlined />}
                  onClick={() => downloadLocal(trainFileRef.current)}>
            下载
          </Button>
        </Space>
      )}

      {/* 开始训练 */}
      <Button type="primary"
              onClick={startTrain}
              loading={loading}
              disabled={!trainName || !tagPath}
              style={{ marginTop: 16 }}>
        开始训练
      </Button>

      {/* 结果展示 */}
      {topResult && (
        <Card title="训练结果" style={{ marginTop: 24 }}>
          <p><b>Name :</b> {topResult.name}</p>
          <p><b>Top_Tags :</b> {JSON.stringify(topResult.Top_Tags)}</p>
          <p><b>Top_Params :</b> {JSON.stringify(topResult.Top_Params)}</p>
        </Card>
      )}
    </Card>
  );
};

export default Top_Training;
