/* ==================== Training.tsx（整文件覆写）==================== */
/* 作用：
   1. 在本页即可上传 / 切换 Tag.json（→ /api/upload-tag-json）
      - 后端返回 active_file = 绝对路径，把它记在 tagPath
   2. 上传训练数据集 → 点击“开始训练”
      - 只把 data_json + tag_path 发给 /api/train
   3. 训练完成后展示 Float_Array
===================================================================== */

import React, { useState, useRef } from 'react';
import { Card, Upload, Button, message } from 'antd';
import { UploadOutlined } from '@ant-design/icons';
import type { UploadProps, UploadFile } from 'antd';
import type { RcFile } from 'antd/es/upload';
import axios from 'axios';

/* -------------------- 组件主体 -------------------- */
const Training: React.FC = () => {
  /* ===== Tag 文件状态 ===== */
  const [tagFileName, setTagFileName] = useState<string | null>(null); // Tag 文件名
  const [tagPath, setTagPath]       = useState<string | null>(null);   // Tag 绝对路径
  const [uploadingTag, setUploadingTag] = useState(false);             // Tag 上传中

  /* ===== 训练数据集状态 ===== */
  const trainFileRef = useRef<RcFile | null>(null);                    // 训练数据文件
  const [trainFileName, setTrainFileName] = useState<string | null>(null);

  /* ===== 训练过程状态 ===== */
  const [loadingTrain, setLoadingTrain] = useState(false);             // 训练按钮 Loading
  const [trainResult, setTrainResult]   = useState<any>(null);         // 训练结果

  /* **************************************************************** */

  /* ① 上传 / 切换 Tag.json ---------------------------------------- */
  const handleTagUpload = async (option: any) => {
    const { file, onSuccess, onError } = option;
    setUploadingTag(true);
    try {
      const fd = new FormData();
      fd.append('file', file as Blob, (file as any).name);

      /* 调用后端接口 */
      const { data } = await axios.post('/api/upload-tag-json', fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setTagFileName((file as RcFile).name);     // 显示文件名
      setTagPath(data.active_file);              // 记录绝对路径 ⭐
      message.success(`${file.name} 已设为当前 Tag`);
      onSuccess && onSuccess({}, {} as any);
    } catch (err: any) {
      message.error(`Tag 上传失败：${err?.response?.data?.detail || err.message}`);
      onError && onError(err);
    } finally {
      setUploadingTag(false);
    }
  };

  /* ② 点击“开始训练” --------------------------------------------- */
  const handleTrain = async () => {
    if (!tagPath) {                              // 必须先有 Tag
      message.error('请先上传 / 选择 Tag 文件');
      return;
    }
    const dataFile = trainFileRef.current;
    if (!dataFile) {
      message.error('请先上传训练数据集');
      return;
    }

    setLoadingTrain(true);
    try {
      /* 2-A. 读取训练数据文件，自动识别编码 */
      const buf = await dataFile.arrayBuffer();
      let jsonStr: string;
      try {
        jsonStr = new TextDecoder('utf-8').decode(buf);
        JSON.parse(jsonStr);
      } catch {
        jsonStr = new TextDecoder('utf-16le').decode(buf);
      }
      const jsonData = JSON.parse(jsonStr);     // { name, input_x }

      /* 2-B. 组装 FormData，只发 data_json + tag_path */
      const fd = new FormData();
      fd.append('data_json', JSON.stringify(jsonData));
      fd.append('tag_path', tagPath);            // ⭐ 发送给后端

      const { data } = await axios.post('/api/train', fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      /* 2-C. 展示训练结果 */
      message.success('训练成功！');
      setTrainResult(data.float_array ?? data.parameters);
    } catch (err: any) {
      message.error(`训练失败：${err?.response?.data?.detail || err.message}`);
      setTrainResult(null);
    } finally {
      setLoadingTrain(false);
    }
  };

  /* ③ Tag Upload 组件配置 ---------------------------------------- */
  const tagUploadProps: UploadProps = {
    accept: '.json',
    maxCount: 1,
    customRequest: handleTagUpload,
    onRemove: () => { setTagFileName(null); setTagPath(null); return true; },
    fileList: tagFileName ? [{
      uid: 'tag',
      name: tagFileName,
      status: 'done' as UploadFile['status'],
    }] : [],
  };

  /* ④ 训练数据集 Upload 配置 ------------------------------------- */
  const dataUploadProps: UploadProps = {
    accept: '.json',
    maxCount: 1,
    customRequest: async (option: any) => {
      const { file, onSuccess } = option;
      trainFileRef.current = file as RcFile;
      setTrainFileName((file as RcFile).name);
      message.success(`${file.name} 已就绪，点击“开始训练”`);
      onSuccess && onSuccess({}, {} as any);
    },
    onRemove: () => { trainFileRef.current = null; setTrainFileName(null); return true; },
    fileList: trainFileName ? [{
      uid: 'data',
      name: trainFileName,
      status: 'done' as UploadFile['status'],
    }] : [],
  };

  /* -------------------- JSX -------------------- */
  return (
    <Card title="参数训练">
      {/* 上传 / 切换 Tag */}
      <Upload {...tagUploadProps}>
        <Button icon={<UploadOutlined />} loading={uploadingTag}
                disabled={uploadingTag || !!tagFileName}>
          上传 / 切换 Tag 文件
        </Button>
      </Upload>
      {tagFileName && <p style={{ marginTop: 8 }}>当前 Tag: {tagFileName}</p>}

      {/* 上传训练数据集 */}
      <Upload {...dataUploadProps} style={{ marginTop: 16 }}>
        <Button icon={<UploadOutlined />} disabled={!!trainFileName}>
          上传训练数据集
        </Button>
      </Upload>
      {trainFileName && <p style={{ marginTop: 8 }}>已选择数据集: {trainFileName}</p>}

      {/* 开始训练 */}
      <Button type="primary"
              onClick={handleTrain}
              loading={loadingTrain}
              disabled={!trainFileName || !tagPath}
              style={{ marginTop: 16 }}>
        开始训练
      </Button>

      {/* 训练结果 */}
      {trainResult && (
        <Card title="训练结果" style={{ marginTop: 24 }}>
          <p><b>Float_Array :</b> {JSON.stringify(trainResult)}</p>
        </Card>
      )}
    </Card>
  );
};

export default Training;
